import axios from "axios";
import { API_URL } from "./env";

axios.defaults.baseURL = API_URL;

axios.interceptors.request.use(
  (config) => {
    config.headers.authorization = `Bearer ${localStorage.getItem(
      "accessToken"
    )}`;
  },
  (err) => {}
);

export default axios;
